import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { getNotes, getTicketStatus, createNote } from '../../services/notesService';
import axios from 'axios';

const MeetingScreen = () => {
  const [meetingNotes, setMeetingNotes] = useState([]);
  const [filter, setFilter] = useState(['Approved', 'For Approval', 'Declined']);
  const [isAccordionOpen, setIsAccordionOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [createForm, setCreateForm] = useState({ title: '', content: '' });
  const [createFocused, setCreateFocused] = useState({ title: false, content: false });
  const [createLoading, setCreateLoading] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  const fetchMeetingNotes = async () => {
    try {
      const response = await getNotes();
      const allNotes = response.data;

      const notesWithStatus = await Promise.all(
        allNotes.map(async (note) => {
          try {
            const res = await getTicketStatus(note._id);
            const ticketStatus = res.data.ticket[0]?.status || 'Unavailable';
            return { ...note, ticketStatus };
          } catch (err) {
            return { ...note, ticketStatus: 'Unavailable' };
          }
        })
      );

      setMeetingNotes(notesWithStatus);
    } catch (err) {
      console.error('Error fetching notes:', err);
    }
  };

  useEffect(() => {
    fetchMeetingNotes();
  }, [location]);

  const handleNoteClick = (noteId) => {
    navigate(`/minute_note_detail/${noteId}`);
  };

  const formatDate = (isoString) => {
    const date = new Date(isoString);
    return date.toLocaleString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const toggleFilter = (status) => {
    setFilter((prevFilter) =>
      prevFilter.includes(status)
        ? prevFilter.filter((item) => item !== status)
        : [...prevFilter, status]
    );
  };

  const handleRefreshClick = () => {
    const refreshButton = document.querySelector('.refresh-btn');
    if (refreshButton) {
      refreshButton.classList.add('animate');
      setTimeout(() => refreshButton.classList.remove('animate'), 500);
    }
    fetchMeetingNotes();
  };

  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    setCreateLoading(true);
    const noteData = {
      title: createForm.title.trim(),
      content: createForm.content.trim(),
      isMinute: true,
      attendees: null,
    };
    try {
      const createdNote = await createNote(noteData);
      if (!createdNote || !createdNote._id) {
        throw new Error('Note ID not returned from server');
      }
      await axios.post('https://express-auro.onrender.com/api/ticket/create/mnas', {
        reference_id: createdNote._id,
        title: createdNote.title,
      });
      setCreateForm({ title: '', content: '' });
      setShowCreateModal(false);
      setCreateLoading(false);
      fetchMeetingNotes();
    } catch (err) {
      setCreateLoading(false);
      alert(err.response?.data?.message || 'Failed to create note or send to ticket API.');
    }
  };

  const filteredNotes = meetingNotes.filter(
    (note) =>
      filter.includes(note.ticketStatus) &&
      note.title.toLowerCase().startsWith(searchQuery.toLowerCase())
  );

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      backgroundColor: '#141414',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      overflowY: 'auto',
      padding: '2rem',
      boxSizing: 'border-box'
    }}>
      {showCreateModal && (
        <div style={{
          position: 'fixed',
          top: 0, left: 0, width: '100vw', height: '100vh',
          background: 'rgba(0,0,0,0.45)',
          zIndex: 1000,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}>
          <form
            onSubmit={handleCreateSubmit}
            style={{
              position: 'relative',
              width: '90%',
              maxWidth: '600px',
              padding: '2.5rem',
              borderRadius: '12px',
              backgroundColor: 'rgba(51, 65, 85, 0.95)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              zIndex: 1001
            }}
          >
            <button
              type="button"
              onClick={() => setShowCreateModal(false)}
              style={{
                position: 'absolute',
                top: '1rem',
                right: '1rem',
                background: 'none',
                border: 'none',
                color: '#fff',
                fontSize: '1.5rem',
                cursor: 'pointer'
              }}
              aria-label="Close"
            >Ã—</button>
            <h2 style={{
              color: '#f8fafc',
              textAlign: 'center',
              marginBottom: '2rem',
              fontSize: '1.75rem',
              fontWeight: '600'
            }}>Create Meeting Note</h2>
            <div style={{
              position: 'relative',
              marginBottom: '2rem'
            }}>
              <div style={{
                position: 'absolute',
                bottom: 0,
                left: 0,
                right: 0,
                height: '2px',
                backgroundColor: 'rgba(255, 255, 255, 0.2)',
                zIndex: 1
              }} />
              <div style={{
                position: 'absolute',
                bottom: 0,
                left: 0,
                right: 0,
                height: '2px',
                backgroundColor: '#3b82f6',
                zIndex: 2,
                transform: createFocused.title ? 'scaleX(1)' : 'scaleX(0)',
                transformOrigin: 'center',
                transition: 'transform 0.3s ease'
              }} />
              <input
                type="text"
                value={createForm.title}
                onChange={(e) => setCreateForm({ ...createForm, title: e.target.value })}
                onFocus={() => setCreateFocused({ ...createFocused, title: true })}
                onBlur={() => setCreateFocused({ ...createFocused, title: createForm.title.length > 0 })}
                required
                style={{
                  width: '100%',
                  padding: '0.5rem 0',
                  backgroundColor: 'transparent',
                  border: 'none',
                  borderBottom: 'none',
                  color: '#f8fafc',
                  fontSize: '1rem',
                  outline: 'none',
                  transition: 'all 0.3s ease'
                }}
              />
              <label style={{
                position: 'absolute',
                left: 0,
                top: createFocused.title || createForm.title.length > 0 ? '-1.25rem' : '0.5rem',
                color: createFocused.title ? '#3b82f6' : 'rgba(248, 250, 252, 0.7)',
                fontSize: createFocused.title || createForm.title.length > 0 ? '0.75rem' : '1rem',
                transition: 'all 0.3s ease',
                pointerEvents: 'none'
              }}>
                Title
              </label>
            </div>
            <div style={{
              position: 'relative',
              marginBottom: '2.5rem'
            }}>
              <div style={{
                position: 'absolute',
                bottom: 0,
                left: 0,
                right: 0,
                height: '2px',
                backgroundColor: 'rgba(255, 255, 255, 0.2)',
                zIndex: 1
              }} />
              <div style={{
                position: 'absolute',
                bottom: 0,
                left: 0,
                right: 0,
                height: '2px',
                backgroundColor: '#3b82f6',
                zIndex: 2,
                transform: createFocused.content ? 'scaleX(1)' : 'scaleX(0)',
                transformOrigin: 'center',
                transition: 'transform 0.3s ease'
              }} />
              <textarea
                value={createForm.content}
                onChange={(e) => setCreateForm({ ...createForm, content: e.target.value })}
                onFocus={() => setCreateFocused({ ...createFocused, content: true })}
                onBlur={() => setCreateFocused({ ...createFocused, content: createForm.content.length > 0 })}
                required
                style={{
                  width: '100%',
                  height: '200px',
                  padding: '0.5rem 0',
                  backgroundColor: 'transparent',
                  border: 'none',
                  color: '#f8fafc',
                  fontSize: '1rem',
                  outline: 'none',
                  resize: 'none',
                  lineHeight: '1.5',
                  transition: 'all 0.3s ease'
                }}
              />
              <label style={{
                position: 'absolute',
                left: 0,
                top: createFocused.content || createForm.content.length > 0 ? '-1.25rem' : '0.5rem',
                color: createFocused.content ? '#3b82f6' : 'rgba(248, 250, 252, 0.7)',
                fontSize: createFocused.content || createForm.content.length > 0 ? '0.75rem' : '1rem',
                transition: 'all 0.3s ease',
                pointerEvents: 'none'
              }}>
                Content
              </label>
            </div>
            <button
              type="submit"
              disabled={createLoading}
              style={{
                width: '100%',
                padding: '0.875rem',
                borderRadius: '8px',
                border: 'none',
                backgroundColor: '#3b82f6',
                color: 'white',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: createLoading ? 'not-allowed' : 'pointer',
                transition: 'all 0.3s ease',
                marginTop: '1rem',
                opacity: createLoading ? 0.7 : 1
              }}
            >
              {createLoading ? 'Saving...' : 'Save Meeting Note'}
            </button>
          </form>
        </div>
      )}
      <br></br>
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: 0
      }} />

      <div style={{
        position: 'relative',
        maxWidth: '1200px',
        margin: '0 auto',
        zIndex: 1
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '2rem',
          flexWrap: 'wrap',
          gap: '1rem'
        }}>
          <h1 className="wave-text" style={{
            margin: 0,
            fontWeight: '600',
            textShadow: '0 2px 4px rgba(0,0,0,0.3)',
            display: 'flex',
            gap: '0.2rem',
            color: 'white',
          }}>
            {'Meeting Minutes'.split('').map((char, index) => (
              <span key={index} style={{ animationDelay: `${index * 0.2}s` }}>
                {char === ' ' ? '\u00A0' : char} {}
              </span>
            ))} 
          </h1>

          <button
            onClick={() => navigate('/login')}
            className="logout-btn"
          >
            <div className="sign">
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                <path d="M10 9v-2h10v10h-10v-2h-2v4h14v-14h-14v4h2zm-6 3l4-4v3h8v2h-8v3l-4-4z" />
              </svg>
            </div>
            <span className="text">Logout</span>
          </button>
        </div>

        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '1rem',
          flexWrap: 'wrap',
          gap: '1rem'
        }}>
          <div style={{ display: 'flex', gap: '1rem' }}>
            <input
              type="text"
              placeholder="Search by title..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              style={{
                flex: '0 0 300px', 
                padding: '0.5rem 1rem',
                borderRadius: '8px',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                backgroundColor: 'rgba(51, 65, 85, 0.7)',
                color: 'white',
                fontSize: '1rem',
                outline: 'none',
                transition: 'all 0.3s ease',
              }}
            />

            <button
              onClick={() => setShowCreateModal(true)}
              className="button"
            >
              <span>Create</span>
            </button>

            <button
              onClick={handleRefreshClick}
              className="refresh-btn"
            >
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M23 4v6h-6"></path>
                <path d="M1 20v-6h6"></path>
                <path d="M3.51 9a9 9 0 0115.03-4.36L23 10M1 14l4.47 5.36A9 9 0 0020.49 15"></path>
              </svg>
              Refresh
            </button>
          </div>

          <button
            onClick={() => setIsAccordionOpen(!isAccordionOpen)}
            style={{
              backgroundColor: '#4973ff',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '0.5rem 1rem',
              fontSize: '1rem',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
            }}
          >
            Status
          </button>
        </div>

        <div
          style={{
            maxHeight: isAccordionOpen ? '200px' : '0',
            opacity: isAccordionOpen ? 1 : 0,
            transform: isAccordionOpen ? 'translateY(0)' : 'translateY(-10px)',
            overflow: 'hidden',
            padding: isAccordionOpen ? '1rem 0' : '0',
            transition: 'max-height 0.5s ease, opacity 0.5s ease, transform 0.5s ease, padding 0.5s ease',
          }}
        >
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button
              onClick={() => toggleFilter('Approved')}
              style={{
                backgroundColor: filter.includes('Approved') ? '#4973ff' : '#334155b3',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                padding: '0.5rem 1rem',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
              }}
            >
              Approved
            </button>
            <button
              onClick={() => toggleFilter('For Approval')}
              style={{
                backgroundColor: filter.includes('For Approval') ? '#4973ff' : '#334155b3',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                padding: '0.5rem 1rem',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
              }}
            >
              For Approval
            </button>
            <button
              onClick={() => toggleFilter('Declined')}
              style={{
                backgroundColor: filter.includes('Declined') ? '#4973ff' : '#334155b3',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                padding: '0.5rem 1rem',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
              }}
            >
              Declined
            </button>
          </div>
        </div>

        <br></br>
        {filteredNotes.length === 0 ? (
          <div style={{
            backgroundColor: 'rgba(51, 65, 85, 0.5)',
            borderRadius: '12px',
            padding: '2rem',
            textAlign: 'center',
            color: 'rgba(248, 250, 252, 0.7)',
            border: '1px dashed rgba(255,255,255,0.1)'
          }}>
            <p style={{ margin: 0, fontSize: '1.1rem' }}>
              No meeting minutes found
            </p>
          </div>
        ) : (
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
            gap: '1.5rem',
            marginTop: '1rem'
          }}>
            {filteredNotes.map(note => (
              <div
                key={note._id}
                onClick={() => handleNoteClick(note._id)}
                className="note-card"
              >
                <h3 style={{
                  color: '#f8fafc',
                  marginTop: 0,
                  marginBottom: '1rem',
                  fontSize: '1.25rem',
                  fontWeight: '600',
                  whiteSpace: 'nowrap',
                  overflow: 'hidden',
                  textOverflow: 'ellipsis'
                }}>
                  {note.title}
                </h3>
                <div style={{
                  display: 'inline-block',
                  backgroundColor: note.ticketStatus === 'For Approval' ? 'rgba(255, 223, 0, 0.2)' :
                    note.ticketStatus === 'Declined' ? 'rgba(255, 0, 0, 0.2)' :
                      'rgba(59, 130, 246, 0.2)',
                  color: note.ticketStatus === 'For Approval' ? '#FFD700' :
                    note.ticketStatus === 'Declined' ? '#FF0000' :
                      '#93c5fd',
                  padding: '0.25rem 0.5rem',
                  borderRadius: '4px',
                  fontSize: '0.75rem',
                  marginBottom: '0.5rem'
                }}>
                  {note.ticketStatus}
                </div>
                <div style={{
                  color: 'rgba(248, 250, 252, 0.6)',
                  fontSize: '0.85rem'
                }}>
                  {formatDate(note.createdAt)}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default MeetingScreen;
