import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNoteById, updateNote, deleteNote } from '../../services/notesService';
import { getAllUsers } from '../../services/userService';

const MinuteNoteDetail = () => {
  const { id } = useParams();
  const [note, setNote] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedAttendees, setSelectedAttendees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editedContent, setEditedContent] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNoteAndUsers = async () => {
      try {
        const noteResponse = await getNoteById(id);
        const usersResponse = await getAllUsers();

        const sortedUsers = usersResponse.data.users.sort((a, b) =>
          a.firstName.localeCompare(b.firstName)
        );

        setNote(noteResponse.data);
        setEditedContent(noteResponse.data.content);
        setUsers(sortedUsers);

        if (noteResponse.data.attendees) {
          const attendeeIds = noteResponse.data.attendees.map(String);
          setSelectedAttendees(attendeeIds);
        }
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchNoteAndUsers();
  }, [id]);

  const handleSave = async () => {
    try {
      await updateNote(id, {
        content: editedContent,
        attendees: selectedAttendees
      });
      const updatedNote = await getNoteById(id);
      setNote(updatedNote.data);
      setIsEditing(false);
    } catch (err) {
      console.error('Error saving note:', err);
    }
  };

  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete this note?")) {
      try {
        await deleteNote(id);
        navigate('/meeting_screen');
      } catch (err) {
        console.error('Error deleting note:', err);
      }
    }
  };

  const handleAttendeeChange = (userId) => {
    if (!isEditing) return;
    const idStr = String(userId);
    setSelectedAttendees((prev) =>
      prev.includes(idStr)
        ? prev.filter((id) => id !== idStr)
        : [...prev, idStr]
    );
  };

  const handleMarkAll = () => {
    if (!isEditing) return;
    if (selectedAttendees.length === users.length) {
      setSelectedAttendees([]);
    } else {
      setSelectedAttendees(users.map((user) => String(user.id)));
    }
  };

  const handleBack = () => {
    navigate('/meeting_screen');
  };

  if (!note || !users.length) return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      backgroundColor: '#141414',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    }}>
      <div className="loader"></div>
    </div>
  );

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      backgroundColor: '#141414',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      overflow: 'hidden'
    }}>
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backdropFilter: 'blur(8px)',
        WebkitBackdropFilter: 'blur(8px)',
        zIndex: 0
      }} />

      <div style={{
        position: 'relative',
        height: 'calc(100vh - 4rem)',
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '2rem',
        boxSizing: 'border-box',
        zIndex: 1,
        display: 'flex',
        flexDirection: 'column'
      }}>
        <button
          onClick={handleBack}
          style={{
            backgroundColor: 'rgba(51, 65, 85, 0.7)',
            color: 'rgba(248, 250, 252, 0.9)',
            border: '1px solid rgba(255,255,255,0.2)',
            borderRadius: '8px',
            padding: '0.5rem 1rem',
            fontSize: '1rem',
            fontWeight: '600',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            marginBottom: '1.5rem',
            display: 'inline-flex',
            alignItems: 'center',
            gap: '0.5rem',
            width: 'fit-content',
            ':hover': {
              backgroundColor: 'rgba(51, 65, 85, 0.9)',
              transform: 'translateY(-2px)'
            }
          }}
        >
          ‚Üê Back to Meeting Notes
        </button>

        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '2rem',
          flexWrap: 'wrap',
          gap: '1rem'
        }}>
          <h1 className="wave-text" style={{
            margin: 0,
            fontWeight: '600',
            textShadow: '0 2px 4px rgba(0,0,0,0.3)',
            display: 'flex',
            gap: '0.2rem',
            color: 'white',
          }}>
            {note.title.split('').map((char, index) => (
              <span key={index} style={{ animationDelay: `${index * 0.2}s` }}>
                {char === ' ' ? '\u00A0' : char}
              </span>
            ))}
          </h1>

          <div style={{ display: 'flex', gap: '1rem', position: 'relative' }}>
            {isEditing ? (
              <>
                {/* Cancel Button */}
                <button
                  onClick={() => setIsEditing(false)}
                  style={{
                    width: 150,
                    height: 50,
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    background: '#e62222',
                    border: 'none',
                    borderRadius: 5,
                    boxShadow: '1px 1px 3px rgba(0,0,0,0.15)',
                    position: 'relative',
                    fontFamily: "'Poppins', sans-serif",
                    transition: '200ms'
                  }}
                >
                  <span style={{
                    transform: 'translateX(35px)',
                    color: 'white',
                    fontWeight: 'bold',
                    transition: '200ms'
                  }}>Cancel</span>
                  <span style={{
                    position: 'absolute',
                    borderLeft: '1px solid #c41b1b',
                    transform: 'translateX(110px)',
                    height: 40,
                    width: 40,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    transition: '200ms'
                  }}>
                    {/* X icon */}
                    <svg width="15" height="15" viewBox="0 0 15 15" fill="none">
                      <path d="M2 2L13 13M13 2L2 13" stroke="#eee" strokeWidth="2" strokeLinecap="round"/>
                    </svg>
                  </span>
                </button>
                {/* Save Button */}
                <button
                  onClick={handleSave}
                  style={{
                    width: 150,
                    height: 50,
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    background: '#4973ff',
                    border: 'none',
                    borderRadius: 5,
                    boxShadow: '1px 1px 3px rgba(0,0,0,0.15)',
                    position: 'relative',
                    fontFamily: "'Poppins', sans-serif",
                    transition: '200ms'
                  }}
                >
                  <span style={{
                    transform: 'translateX(35px)',
                    color: 'white',
                    fontWeight: 'bold',
                    transition: '200ms'
                  }}>Save</span>
                  <span style={{
                    position: 'absolute',
                    borderLeft: '1px solid #334155',
                    transform: 'translateX(110px)',
                    height: 40,
                    width: 40,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    transition: '200ms'
                  }}>
                    {/* Check icon */}
                    <svg width="15" height="15" viewBox="0 0 15 15" fill="none">
                      <path d="M4 8L7 11L11 5" stroke="#eee" strokeWidth="2" strokeLinecap="round"/>
                    </svg>
                  </span>
                </button>
              </>
            ) : (
              <>
                {/* Edit Button */}
                <button
                  onClick={() => setIsEditing(true)}
                  style={{
                    width: 150,
                    height: 50,
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    background: '#4973ff',
                    border: 'none',
                    borderRadius: 5,
                    boxShadow: '1px 1px 3px rgba(0,0,0,0.15)',
                    position: 'relative',
                    fontFamily: "'Poppins', sans-serif",
                    transition: '200ms'
                  }}
                >
                  <span style={{
                    transform: 'translateX(35px)',
                    color: 'white',
                    fontWeight: 'bold',
                    transition: '200ms'
                  }}>Edit</span>
                  <span style={{
                    position: 'absolute',
                    borderLeft: '1px solid #334155',
                    transform: 'translateX(110px)',
                    height: 40,
                    width: 40,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    transition: '200ms'
                  }}>
                    {/* Pencil/Edit icon */}
                    <svg width="15" height="15" viewBox="0 0 15 15" fill="none">
                      <path d="M11.5 2.5L12.5 3.5C13.1667 4.16667 13.1667 5.16667 12.5 5.83333L5 13.3333L2 14L2.66667 11L10.1667 3.5C10.8333 2.83333 11.5 2.5 11.5 2.5Z" stroke="#eee" strokeWidth="1.5" strokeLinejoin="round"/>
                    </svg>
                  </span>
                </button>
                {/* Delete Button */}
                <button
                  onClick={handleDelete}
                  style={{
                    width: 150,
                    height: 50,
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    background: '#e62222',
                    border: 'none',
                    borderRadius: 5,
                    boxShadow: '1px 1px 3px rgba(0,0,0,0.15)',
                    position: 'relative',
                    fontFamily: "'Poppins', sans-serif",
                    transition: '200ms'
                  }}
                >
                  <span style={{
                    transform: 'translateX(35px)',
                    color: 'white',
                    fontWeight: 'bold',
                    transition: '200ms'
                  }}>Delete</span>
                  <span style={{
                    position: 'absolute',
                    borderLeft: '1px solid #c41b1b',
                    transform: 'translateX(110px)',
                    height: 40,
                    width: 40,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    transition: '200ms'
                  }}>
                    {/* Trash icon */}
                    <svg width="15" height="15" viewBox="0 0 15 15" fill="none">
                      <path d="M3 5.5V12.5C3 13.0523 3.44772 13.5 4 13.5H11C11.5523 13.5 12 13.0523 12 12.5V5.5M5 7.5V11.5M7.5 7.5V11.5M10 7.5V11.5M2 5.5H13M6 2.5H9C9.55228 2.5 10 2.94772 10 3.5V4.5H5V3.5C5 2.94772 5.44772 2.5 6 2.5Z" stroke="#eee" strokeWidth="1.5" strokeLinejoin="round"/>
                    </svg>
                  </span>
                </button>
              </>
            )}
          </div>
        </div>

        <div style={{
          display: 'grid',
          gridTemplateColumns: '1fr 350px',
          gap: '2rem',
          flex: 1,
          minHeight: 0
        }}>
          <div style={{
            backgroundColor: 'transparent',
            padding: '2rem',
            borderRadius: '12px',
            border: '1px solid rgba(255,255,255,0.1)',
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden'
          }}>
            <h2 style={{
              color: '#f8fafc',
              fontSize: '1.25rem',
              fontWeight: '600',
              marginTop: 0,
              marginBottom: '1.5rem',
              borderBottom: '1px solid rgba(255,255,255,0.1)',
              paddingBottom: '0.75rem'
            }}>
              Meeting Minutes
            </h2>

            {isEditing ? (
              <textarea
                value={editedContent}
                onChange={(e) => setEditedContent(e.target.value)}
                style={{
                  flex: 1,
                  width: '100%',
                  padding: '1rem',
                  borderRadius: '8px',
                  border: 'none',
                  backgroundColor: 'transparent',
                  color: '#f8fafc',
                  fontSize: '1rem',
                  outline: 'none',
                  lineHeight: '1.6',
                  resize: 'none',
                  fontFamily: 'inherit',
                  '::placeholder': {
                    color: 'rgba(248, 250, 252, 0.5)'
                  }
                }}
              />
            ) : (
              <div
                className="minute-note-detail-scroll"
                style={{
                  flex: 1,
                  color: 'rgba(248, 250, 252, 0.9)',
                  lineHeight: '1.6',
                  whiteSpace: 'pre-wrap',
                  paddingRight: '0.5rem',
                }}
              >
                {note.content}
              </div>
            )}
          </div>

          <div style={{
            backgroundColor: 'transparent',
            padding: '2rem',
            borderRadius: '12px',
            border: '1px solid rgba(255,255,255,0.1)',
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '1.5rem',
              borderBottom: '1px solid rgba(255,255,255,0.1)',
              paddingBottom: '0.75rem'
            }}>
              <h2 style={{
                color: '#f8fafc',
                fontSize: '1.25rem',
                fontWeight: '600',
                margin: 0
              }}>
                Attendees
              </h2>

              {isEditing && (
                <button
                  onClick={handleMarkAll}
                  style={{
                    backgroundColor: 'rgba(59, 130, 246, 0.2)',
                    color: '#93c5fd',
                    border: 'none',
                    borderRadius: '8px',
                    padding: '0.5rem 1rem',
                    fontSize: '0.9rem',
                    fontWeight: '500',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    ':hover': {
                      backgroundColor: 'rgba(59, 130, 246, 0.4)'
                    }
                  }}
                >
                  {selectedAttendees.length === users.length ? 'Unselect All' : 'Select All'}
                </button>
              )}
            </div>

            <div
              className="minute-note-detail-scroll"
              style={{
                flex: 1,
                paddingRight: '0.5rem',
              }}
            >
              {/* Selected Attendees */}
              <div style={{
                marginBottom: '1rem',
                padding: '1rem',
                borderRadius: '8px',
                backgroundColor: 'rgba(59, 130, 246, 0.1)',
                border: '1px solid rgba(59, 130, 246, 0.3)'
              }}>
                <h3 style={{
                  color: '#f8fafc',
                  fontSize: '1rem',
                  fontWeight: '600',
                  marginBottom: '0.5rem'
                }}>
                  Selected Attendees
                </h3>
                <ul style={{
                  listStyleType: 'none',
                  padding: 0,
                  display: 'grid',
                  gap: '0.5rem'
                }}>
                  {users.filter(user => selectedAttendees.includes(String(user.id))).map(user => (
                    <li
                      key={user.id}
                      onClick={() => handleAttendeeChange(user.id)}
                      style={{
                        display: 'flex',
                        alignItems: 'center',
                        padding: '0.75rem',
                        borderRadius: '8px',
                        backgroundColor: 'rgba(59, 130, 246, 0.2)',
                        transition: 'all 0.2s ease',
                        cursor: isEditing ? 'pointer' : 'default'
                      }}
                    >
                      <span style={{
                        color: '#f8fafc',
                        fontWeight: '500'
                      }}>
                        {user.firstName} {user.lastName}
                      </span>
                    </li>
                  ))}
                </ul>
              </div>

              {/* Divider */}
              <div style={{
                height: '1px',
                backgroundColor: 'rgba(255, 255, 255, 0.2)',
                margin: '1rem 0'
              }}></div>

              {/* Unselected Attendees */}
              <div style={{
                padding: '1rem',
                borderRadius: '8px',
                backgroundColor: 'rgba(255, 255, 255, 0.05)',
                border: '1px solid rgba(255, 255, 255, 0.1)'
              }}>
                <h3 style={{
                  color: '#f8fafc',
                  fontSize: '1rem',
                  fontWeight: '600',
                  marginBottom: '0.5rem'
                }}>
                  Unselected Attendees
                </h3>
                <ul style={{
                  listStyleType: 'none',
                  padding: 0,
                  display: 'grid',
                  gap: '0.5rem'
                }}>
                  {users.filter(user => !selectedAttendees.includes(String(user.id))).map(user => (
                    <li
                      key={user.id}
                      onClick={() => handleAttendeeChange(user.id)}
                      style={{
                        display: 'flex',
                        alignItems: 'center',
                        padding: '0.75rem',
                        borderRadius: '8px',
                        backgroundColor: 'rgba(255, 255, 255, 0.05)',
                        transition: 'all 0.2s ease',
                        cursor: isEditing ? 'pointer' : 'default'
                      }}
                    >
                      <span style={{
                        color: 'rgba(248, 250, 252, 0.7)',
                        fontWeight: 'normal'
                      }}>
                        {user.firstName} {user.lastName}
                      </span>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MinuteNoteDetail;
