import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNoteById, updateNote, deleteNote } from '../../services/notesService';
import { getAllUsers } from '../../services/userService';

const MinuteNoteDetail = () => {
  const { id } = useParams();
  const [note, setNote] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedAttendees, setSelectedAttendees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editedContent, setEditedContent] = useState('');
  const navigate = useNavigate();

  // Fetch note and users
  useEffect(() => {
    const fetchNoteAndUsers = async () => {
      try {
        const noteResponse = await getNoteById(id);
        const usersResponse = await getAllUsers();

        setNote(noteResponse.data);
        setEditedContent(noteResponse.data.content);
        setUsers(usersResponse.data.users);

        if (noteResponse.data.attendees) {
          const attendeeIds = noteResponse.data.attendees.map(String);
          setSelectedAttendees(attendeeIds);
        }
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchNoteAndUsers();
  }, [id]);

  const handleSave = async () => {
    try {
      await updateNote(id, { 
        content: editedContent,
        attendees: selectedAttendees 
      });
      const updatedNote = await getNoteById(id);
      setNote(updatedNote.data);
      setIsEditing(false);
      alert('Note updated successfully!');
    } catch (err) {
      console.error('Error saving note:', err);
    }
  };

  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete this note?")) {
      try {
        await deleteNote(id);
        alert('Note deleted successfully!');
        navigate('/meeting_screen');
      } catch (err) {
        console.error('Error deleting note:', err);
      }
    }
  };

  const handleAttendeeChange = (userId) => {
    const idStr = String(userId);
    setSelectedAttendees((prev) =>
      prev.includes(idStr)
        ? prev.filter((id) => id !== idStr)
        : [...prev, idStr]
    );
  };

  const handleMarkAll = () => {
    if (selectedAttendees.length === users.length) {
      setSelectedAttendees([]);
    } else {
      setSelectedAttendees(users.map((user) => String(user.id)));
    }
  };

  const handleBack = () => {
    navigate('/meeting_screen');
  };

  if (!note || !users.length) return <div>Loading...</div>;

  return (
    <div style={{ padding: '20px', fontFamily: 'Segoe UI, sans-serif', maxWidth: '800px', margin: '0 auto' }}>
      <button
        onClick={handleBack}
        style={{
          backgroundColor: '#c9c9c9',
          padding: '6px 12px',
          border: 'none',
          borderRadius: '8px',
          marginBottom: '20px',
          cursor: 'pointer'
        }}
      >
        ‚Üê Back to Meeting Notes
      </button>

      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        marginBottom: '20px'
      }}>
        <h1 style={{ fontStyle: 'italic', fontWeight: 'normal', margin: 0 }}>{note.title}</h1>
        
        <div style={{ display: 'flex', gap: '10px' }}>
          <button
            onClick={() => setIsEditing(!isEditing)}
            style={{
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '6px 12px',
              cursor: 'pointer'
            }}
          >
            {isEditing ? 'Cancel Edit' : 'Edit'}
          </button>
          
          <button
            onClick={handleDelete}
            style={{
              backgroundColor: '#f44336',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '6px 12px',
              cursor: 'pointer'
            }}
          >
            Delete
          </button>
        </div>
      </div>

      {isEditing ? (
        <textarea
          value={editedContent}
          onChange={(e) => setEditedContent(e.target.value)}
          style={{
            width: '100%',
            minHeight: '200px',
            padding: '15px',
            borderRadius: '8px',
            border: '1px solid #ccc',
            fontSize: '16px',
            marginBottom: '20px'
          }}
        />
      ) : (
        <div
          style={{
            backgroundColor: '#e0ecff',
            padding: '20px',
            borderRadius: '10px',
            fontStyle: 'italic',
            minHeight: '100px',
            marginBottom: '20px'
          }}
        >
          {note.content}
        </div>
      )}

      <div style={{ 
        backgroundColor: '#f5f5f5', 
        padding: '20px', 
        borderRadius: '10px',
        marginBottom: '20px'
      }}>
        <h3 style={{ marginTop: '0' }}>Attendees:</h3>
        <button
          onClick={handleMarkAll}
          style={{
            marginBottom: '15px',
            padding: '6px 12px',
            backgroundColor: '#b4d8f7',
            border: 'none',
            borderRadius: '10px',
            cursor: 'pointer'
          }}
        >
          {selectedAttendees.length === users.length ? 'Unmark All' : 'Mark All'}
        </button>

        <ul style={{ 
          listStyleType: 'none', 
          paddingLeft: '0',
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
          gap: '10px'
        }}>
          {users.map((user) => (
            <li key={user.id} style={{ marginBottom: '10px' }}>
              <label style={{ 
                display: 'flex', 
                alignItems: 'center',
                cursor: 'pointer'
              }}>
                <input
                  type="checkbox"
                  checked={selectedAttendees.includes(String(user.id))}
                  onChange={() => handleAttendeeChange(user.id)}
                  style={{ 
                    marginRight: '10px',
                    width: '18px',
                    height: '18px',
                    cursor: 'pointer'
                  }}
                  disabled={!isEditing}
                />
                {user.firstName} {user.lastName}
              </label>
            </li>
          ))}
        </ul>
      </div>

      {isEditing && (
        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
          <button
            onClick={() => setIsEditing(false)}
            style={{
              backgroundColor: '#f5f5f5',
              border: '1px solid #ccc',
              borderRadius: '8px',
              padding: '8px 16px',
              cursor: 'pointer'
            }}
          >
            Cancel
          </button>
          <button
            onClick={handleSave}
            style={{
              backgroundColor: '#2196F3',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '8px 16px',
              cursor: 'pointer'
            }}
          >
            Save Changes
          </button>
        </div>
      )}
    </div>
  );
};

export default MinuteNoteDetail;
