import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNoteById, updateNote, deleteNote } from '../../services/notesService';
import { getAllUsers } from '../../services/userService';
import background from '../../assets/background.png';

const MinuteNoteDetail = () => {
  const { id } = useParams();
  const [note, setNote] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedAttendees, setSelectedAttendees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editedContent, setEditedContent] = useState('');
  const navigate = useNavigate();

  // Fetch note and users
  useEffect(() => {
    const fetchNoteAndUsers = async () => {
      try {
        const noteResponse = await getNoteById(id);
        const usersResponse = await getAllUsers();

        setNote(noteResponse.data);
        setEditedContent(noteResponse.data.content);
        setUsers(usersResponse.data.users);

        if (noteResponse.data.attendees) {
          const attendeeIds = noteResponse.data.attendees.map(String);
          setSelectedAttendees(attendeeIds);
        }
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchNoteAndUsers();
  }, [id]);

  const handleSave = async () => {
    try {
      await updateNote(id, { 
        content: editedContent,
        attendees: selectedAttendees 
      });
      const updatedNote = await getNoteById(id);
      setNote(updatedNote.data);
      setIsEditing(false);
    } catch (err) {
      console.error('Error saving note:', err);
    }
  };

  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete this note?")) {
      try {
        await deleteNote(id);
        navigate('/meeting_screen');
      } catch (err) {
        console.error('Error deleting note:', err);
      }
    }
  };

  const handleAttendeeChange = (userId) => {
    if (!isEditing) return;
    const idStr = String(userId);
    setSelectedAttendees((prev) =>
      prev.includes(idStr)
        ? prev.filter((id) => id !== idStr)
        : [...prev, idStr]
    );
  };

  const handleMarkAll = () => {
    if (!isEditing) return;
    if (selectedAttendees.length === users.length) {
      setSelectedAttendees([]);
    } else {
      setSelectedAttendees(users.map((user) => String(user.id)));
    }
  };

  const handleBack = () => {
    navigate('/meeting_screen');
  };

  if (!note || !users.length) return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      backgroundImage: `url(${background})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      color: '#f8fafc'
    }}>
      Loading...
    </div>
  );

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      backgroundImage: `url(${background})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      overflow: 'hidden'
    }}>
      {/* Blurred background overlay */}
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backdropFilter: 'blur(8px)',
        WebkitBackdropFilter: 'blur(8px)',
        zIndex: 0
      }} />
      
      {/* Content container */}
      <div style={{
        position: 'relative',
        display: 'flex',
        height: '100vh',
        zIndex: 1
      }}>
        {/* Left side - Note content */}
        <div style={{
          width: '50%',
          padding: '2rem',
          overflowY: 'auto',
          boxSizing: 'border-box'
        }}>
          {/* Back button */}
          <button
            onClick={handleBack}
            style={{
              backgroundColor: 'rgba(51, 65, 85, 0.7)',
              color: 'rgba(248, 250, 252, 0.9)',
              border: '1px solid rgba(255,255,255,0.2)',
              borderRadius: '8px',
              padding: '0.75rem 1.5rem',
              fontSize: '1rem',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              marginBottom: '1.5rem',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              ':hover': {
                backgroundColor: 'rgba(51, 65, 85, 0.9)',
                transform: 'translateY(-2px)'
              }
            }}
          >
            ‚Üê Back to Meeting Notes
          </button>

          {/* Header section */}
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            marginBottom: '2rem',
            flexWrap: 'wrap',
            gap: '1rem'
          }}>
            <h1 style={{ 
              color: '#f8fafc',
              fontSize: '1.8rem',
              fontWeight: '600',
              margin: 0,
              textShadow: '0 2px 4px rgba(0,0,0,0.3)'
            }}>
              {note.title}
            </h1>
            
            <div style={{ display: 'flex', gap: '1rem' }}>
              <button
                onClick={() => setIsEditing(!isEditing)}
                style={{
                  backgroundColor: isEditing ? 'rgba(51, 65, 85, 0.7)' : 'rgba(59, 130, 246, 0.9)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  padding: '0.75rem 1.5rem',
                  fontSize: '1rem',
                  fontWeight: '600',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  ':hover': {
                    backgroundColor: isEditing ? 'rgba(51, 65, 85, 0.9)' : '#2563eb',
                    transform: 'translateY(-2px)'
                  }
                }}
              >
                {isEditing ? 'Cancel Edit' : 'Edit'}
              </button>
              
              <button
                onClick={handleDelete}
                style={{
                  backgroundColor: 'rgba(220, 38, 38, 0.9)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  padding: '0.75rem 1.5rem',
                  fontSize: '1rem',
                  fontWeight: '600',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  ':hover': {
                    backgroundColor: 'rgba(185, 28, 28, 0.9)',
                    transform: 'translateY(-2px)'
                  }
                }}
              >
                Delete
              </button>
            </div>
          </div>

          {/* Content section */}
          {isEditing ? (
            <textarea
              value={editedContent}
              onChange={(e) => setEditedContent(e.target.value)}
              style={{
                width: '100%',
                minHeight: 'calc(100vh - 300px)',
                padding: '1.5rem',
                borderRadius: '12px',
                border: '1px solid rgba(255,255,255,0.2)',
                backgroundColor: 'rgba(15, 23, 42, 0.5)',
                color: '#f8fafc',
                fontSize: '1rem',
                outline: 'none',
                marginBottom: '2rem',
                lineHeight: '1.6',
                resize: 'none',
                transition: 'all 0.3s ease',
                ':focus': {
                  borderColor: '#3b82f6',
                  boxShadow: '0 0 0 2px rgba(59, 130, 246, 0.3)'
                }
              }}
            />
          ) : (
            <div style={{
              backgroundColor: 'rgba(51, 65, 85, 0.5)',
              padding: '2rem',
              borderRadius: '12px',
              color: 'rgba(248, 250, 252, 0.9)',
              border: '1px solid rgba(255,255,255,0.1)',
              marginBottom: '2rem',
              lineHeight: '1.6',
              whiteSpace: 'pre-wrap',
              minHeight: 'calc(100vh - 300px)',
              overflowY: 'auto'
            }}>
              {note.content}
            </div>
          )}

          {/* Save buttons when editing */}
          {isEditing && (
            <div style={{ 
              display: 'flex', 
              justifyContent: 'flex-end', 
              gap: '1rem',
              marginTop: '1rem'
            }}>
              <button
                onClick={() => setIsEditing(false)}
                style={{
                  backgroundColor: 'rgba(51, 65, 85, 0.7)',
                  color: 'rgba(248, 250, 252, 0.9)',
                  border: '1px solid rgba(255,255,255,0.2)',
                  borderRadius: '8px',
                  padding: '0.75rem 1.5rem',
                  fontSize: '1rem',
                  fontWeight: '600',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  ':hover': {
                    backgroundColor: 'rgba(51, 65, 85, 0.9)',
                    transform: 'translateY(-2px)'
                  }
                }}
              >
                Discard Changes
              </button>
              <button
                onClick={handleSave}
                style={{
                  backgroundColor: '#3b82f6',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  padding: '0.75rem 1.5rem',
                  fontSize: '1rem',
                  fontWeight: '600',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  ':hover': {
                    backgroundColor: '#2563eb',
                    transform: 'translateY(-2px)',
                    boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
                  }
                }}
              >
                Save Changes
              </button>
            </div>
          )}
        </div>

        {/* Right side - Attendees */}
        <div style={{
          width: '50%',
          padding: '2rem',
          backgroundColor: 'rgba(30, 41, 59, 0.7)',
          overflowY: 'auto',
          boxSizing: 'border-box',
          borderLeft: '1px solid rgba(255,255,255,0.1)'
        }}>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center', 
            marginBottom: '1.5rem',
            position: 'sticky',
            top: 0,
            backgroundColor: 'rgba(30, 41, 59, 0.9)',
            padding: '1rem 0',
            zIndex: 1
          }}>
            <h2 style={{ 
              color: '#f8fafc',
              margin: 0,
              fontSize: '1.5rem',
              fontWeight: '600'
            }}>
              Attendees
            </h2>
            
            <button
              onClick={handleMarkAll}
              disabled={!isEditing}
              style={{
                backgroundColor: isEditing ? 'rgba(59, 130, 246, 0.2)' : 'rgba(100, 116, 139, 0.2)',
                color: isEditing ? '#93c5fd' : 'rgba(148, 163, 184, 0.7)',
                border: 'none',
                borderRadius: '8px',
                padding: '0.5rem 1rem',
                fontSize: '0.9rem',
                fontWeight: '500',
                cursor: isEditing ? 'pointer' : 'default',
                transition: 'all 0.3s ease',
                ':hover': isEditing ? {
                  backgroundColor: 'rgba(59, 130, 246, 0.4)'
                } : {}
              }}
            >
              {selectedAttendees.length === users.length ? 'Unmark All' : 'Mark All'}
            </button>
          </div>

          <div style={{ 
            display: 'flex',
            flexDirection: 'column',
            gap: '0.5rem'
          }}>
            {users.map((user) => (
              <div 
                key={user.id} 
                onClick={() => handleAttendeeChange(user.id)}
                style={{ 
                  display: 'flex',
                  alignItems: 'center',
                  padding: '0.75rem 1rem',
                  borderRadius: '8px',
                  backgroundColor: selectedAttendees.includes(String(user.id)) 
                    ? 'rgba(59, 130, 246, 0.2)' 
                    : 'rgba(255,255,255,0.05)',
                  transition: 'all 0.2s ease',
                  cursor: isEditing ? 'pointer' : 'default',
                  ':hover': {
                    backgroundColor: isEditing 
                      ? selectedAttendees.includes(String(user.id))
                        ? 'rgba(59, 130, 246, 0.3)'
                        : 'rgba(255,255,255,0.1)'
                      : undefined
                  }
                }}
              >
                <input
                  type="checkbox"
                  checked={selectedAttendees.includes(String(user.id))}
                  onChange={() => handleAttendeeChange(user.id)}
                  style={{ 
                    marginRight: '1rem',
                    width: '18px',
                    height: '18px',
                    cursor: isEditing ? 'pointer' : 'default',
                    accentColor: '#3b82f6',
                    opacity: isEditing ? 1 : 0.7,
                    pointerEvents: isEditing ? 'auto' : 'none'
                  }}
                />
                <span style={{ 
                  color: selectedAttendees.includes(String(user.id)) 
                    ? '#f8fafc' 
                    : 'rgba(248, 250, 252, 0.7)',
                  fontWeight: selectedAttendees.includes(String(user.id)) ? '500' : 'normal'
                }}>
                  {user.firstName} {user.lastName}
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default MinuteNoteDetail;
